    static String highestValuePalindrome(String s, int n, int k) {

        String result="";
        int middleIndex=-1,lc=0;
        char[] cs=s.toCharArray();
        System.out.println(cs.length);
        if(n==1){
            middleIndex=0;
        }else if(n%2==1){
            middleIndex=(n-1)/2;
        }else{
            middleIndex=n/2;
        }
        int fPt=-1,rPt=-1,j=0,changeCnt=0;
        int[] indexArr=new int[n];

        for (int i = 0; i < middleIndex; i++) {
            --n;
            fPt=Integer.valueOf(cs[i]+"");
            rPt=Integer.valueOf(cs[n]+"");

            if(fPt!=rPt){
                if(fPt<9){
                    cs[i]='9';
                    ++changeCnt;
                }
                if(rPt<9){
                    cs[n]='9';
                    ++changeCnt;
                }
            }else{
                if(fPt<9){
                    indexArr[j++]=i;
                }
                if(rPt<9){
                    indexArr[j++]=n;
                }
            }

            if(changeCnt>k){
                result="-1";
                break;
            }else if(changeCnt==k){
                break;
            }
        }

        int rChangeCnt=k-changeCnt;
        if(rChangeCnt>=0){
            boolean middleIndexFlg=false;
            if(cs.length%2==1 && rChangeCnt!=0 && rChangeCnt%2==1){
                middleIndexFlg=true;
                if(middleIndex!=0 && cs.length%2==0){
                    --middleIndex;
                }
                --rChangeCnt;
            }
            for (int i = 0; i < indexArr.length; i++) {

                if(middleIndexFlg){
                    if(Integer.valueOf(cs[middleIndex]+"")<9){
                        cs[middleIndex]='9';
                    }
                }

                if(rChangeCnt==0 || i>=j) {
                    break;
                }

                if(Integer.valueOf(cs[indexArr[i]]+"")<9){
                    cs[indexArr[i]]='9';
                    cs[indexArr[i+1]]='9';
                    i++;
                    rChangeCnt-=2;
                }

            }
        }

        if(!"-1".equalsIgnoreCase(result)){
            result=String.valueOf(cs);
        }

        return result;
    }

